package com.serex.appstorehb

import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import android.widget.ImageView
import androidx.recyclerview.widget.RecyclerView
import kotlinx.android.synthetic.main.product_item.view.*
import com.bumptech.glide.Glide

class ProductAdapter(private var products: ArrayList<Product> = ArrayList()) :

    RecyclerView.Adapter<ProductAdapter.ProductViewHolder>() {

    class ProductViewHolder(view: View) : RecyclerView.ViewHolder(view)

    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): ProductViewHolder {
        val view = LayoutInflater.from(parent.context)
            .inflate(R.layout.product_item, parent, false)
        return ProductViewHolder(view)
    }

    override fun getItemCount(): Int {
        return products.size
    }

    override fun onBindViewHolder(holder: ProductViewHolder, position: Int) {

        holder.itemView.name.text = products[position].name
        holder.itemView.location.text = products[position].location
        holder.itemView.price.text = "$"+products[position].price.toString()

        val imagenView = holder.itemView.image

        Glide.with(holder.itemView.context)
            .load("data:image/jpeg;base64,")
            .placeholder(R.drawable.no_image)
            .into(imagenView)

    }


}